    <div class="podcast-episode" v-for="ep in podcasts">
      <img :src="icon(ep.file)" :alt="'Obal ' + ep.epName" @click="playEpisode(ep)">
      <div>
        <h3 class="ep-title">{{ep.epName}}</h3>
        <p v-html="ep.epDesc"></p>
        <btn icon="play" @click="playEpisode(ep)">Přehrát</btn>
        Vydáno <b>{{date(ep.id)}}</b>
      </div>
    </div>

    <div style="text-align:center">
      <div style="color:white">Jste na konci. Nebo spíš na začátku.</div>
      <btn icon="arrow-up-bold" @click="elevator">Zpátky nahoru</btn>
    </div>
  </section>
</template>

<script>
import API from 'api'
import moment from 'moment'
import cl from 'cloudinary-core'
import nano from '@/scripts/nano-scroll'
function getUrl (src, params) {
  const c = cl.Cloudinary.new({cloud_name: 'bio-senpai'})
  let parameters = {
    fetch_format: 'auto',
    height: 150,
    crop: 'scale',
    ...params
  }
  return c.url(src, parameters)
}

let wtfs = [
  'Podcast aneb pár šílenců mluví o anime a všem dalším, co je napadne...',
  'Podcast aneb pár šílenců se pokouší neumřít smíchy...',
  'Podcast aneb pár šílenců šíří hodinu svého smíchu internetem...',
  'Podcast aneb pár šílenců se hádá u mikrofonu o existenci příběhu v ecchi...'
]

export default {
  submenu: ['Projekty', 'Návrhy', 'Podcast'],
  data () {
    return {
      podcasts: []
    }
  },
  created () {
    this.fetchData()
    this.$emit('update:backdrop', 'podcast/backdrop', {opacity: 15})
  },
  beforeRouteLeave (to, from, next) {
    this.$emit('update:backdrop', '')
    next()
  },
  computed: {
    wtf () {
      return wtfs[Math.floor(Math.random() * wtfs.length)]
    }
  },
  methods: {
    fetchData () {
      this.$emit('error', false)
      new API('podcasts')
        .byIdDesc()
        .call()
        .then(res => {
          this.podcasts = res
        })
        .catch(err => {
          this.$emit('error', err)
        })
    },
    playEpisode (ep) {
      this.$emit('update:audio', `//data.bio-senpai.tk/yoimiru/${ep.file}`)
      this.$emit('update:audio-meta', ep)
    },
    icon (file) {
      return getUrl('podcast/icons/' + file.substr(0, file.length - 4))
    },
    date (id) {
      return moment.unix(parseInt(id.toString().substring(0, 8), 16)).locale('cs').format('D.M.YYYY')
    },
    elevator () {
      nano(0)
    }
  }
}
</script>

<style scoped lang="stylus">
