  max-width 400px
  transition stroke-dashoffset 2s, stroke-width 2s, stroke 2s
  &:hover
    stroke transparent
    stroke-width 15px
    stroke-dashoffset 1200

@keyframes draw
  from
    stroke transparent
    stroke-dashoffset 1200
    stroke-width 15px
    filter drop-shadow(0 0 1em white)

@keyframes fadeIn
  from
    fill transparent

.apple-link
  margin 1.5em 0
  div
    margin-top .5em

.podcast-episode
